import React, { useState } from "react";
import data from "../data";
import { ReactComponent as Star } from "../images/star.svg";


function Movies() {
    const [movies, setMovies] = useState(data);

    function handleStarClick(movieTitle) {
        const newMovies = [...movies];

        const movie = newMovies.find(x => x.title === movieTitle);
        movie.isStarred = !movie.isStarred;

        setMovies(newMovies);
    }
    
    return (
        <div className="movies-card">
            <h1 className="movies-title">Filmes</h1>
            <div className="movies-filters">
                <button className="filter">Todos</button>
                <button className="filter">Ação</button>
                <button className="filter">Romance</button>
                <button className="filter">Ficção Científica</button>
                <button className="filter">Terror</button>
            </div>
            <div className="movies-list">
            {
            data.movies.map(movie =>
                <div className="movie">
                    <Star className={`star ${movie.isStarred ? 'active' : ''}`} onClick={() => handleStarClick(movie.title)} />
                    <img className="movie-image" src={movie.backgroundImg} alt="imagem de capa do filme" />
                    <div className="bottom-image">
                        <div className="movie-info">
                            <h2 className="movie-name">{movie.title}</h2>
                            <p className="stars-count">{movie.starsCount}</p>
                        </div>
                        <div className="bag-info">
                            <p className="bag-title">Sacola</p>
                            <p className="bag-price">R$ {movie.price}</p>
                        </div>
                    </div>
                </div>)
            }
            </div>  
        </div>
    );
}

export default Movies;
