import React from "react";
import { ReactComponent as Star } from "../images/star.svg";
import { ReactComponent as GoldenStar } from "../images/golden-star.svg";


function Movies(props) {
    const background = `url('${props.backgroundImg}') no-repeat center/cover`;
    const movie = props.movie;

    return (
         <div className="movie" style={{ background }}>
            <Star className={`star ${movie.isStarred ? 'active' : ''}`} onClick={() => props.handleClick(props.title)} />
            <div className="movie-info">
                <h3>{movie.title}</h3>
                <div className="movie-stars">
                    <GoldenStar className="golden-star" />
                    {movie.starsCount}
                </div>
            </div>
            <button className="bag" onClick={() => props.handleMovieInBag(movie)}>Sacola
                <span className="bag-price">R$ {movie.price}</span>
            </button>
        </div>
    );
}

export default Movies;
