function Filters(props) {
    return (
        <div className="movie-filter">
                <button className={`filter ${props.filterCategory.length === 0 ? 'filter-active' : ''}`}>Todos</button>
                <button className={`filter ${props.filterCategory.includes('action') ? 'filter-active' : ''}`}>Ação</button>
                <button className={`filter ${props.filterCategory.includes('romance') ? 'filter-active' : ''}`}>Romance</button>
                <button className={`filter ${props.filterCategory.includes('science fiction') ? 'filter-active' : ''}`}>Ficção Científica</button>
                <button className={`filter ${props.filterCategory.includes('horror') ? 'filter-active' : ''}`}>Terror</button>
        </div>
    )
}

export default Filters;
