import Movies from "./components/Movies";
import data from "./data";


function App() {
  const [filtered, setFiltered] = useState("all");
  const [filteredMovies, setFilteredMovies] = useState([]);

  useEffect(() => {
    filterMoviesHandler();
  }, []);

  const filterMoviesHandler = () => {
    switch(data.movies.categories){
      case 'horror':
        setFilteredMovies(data.movies.filter(movie => data.movies.categories === 'horror'));
        break;
    }

    console.log(filteredMovies);
  }

  return (
    <div className="App">
      <Header />
      <TopMovies />
      <Movies setFiltered={setFiltered} />
    </div>
  );
}

